    if(rol == 1){
        //const usuario = await pool.query('select * from empleados where cedula = ?', [cedula])
    }else {
        //const usuario = await pool.query('select * from clientes where cedula = ?', [cedula]) 
    }
    done(null, usuario[0])
})

